(function ($) {	
	var elb_filter_masonry_initialize = function ($scope, $) {
		$('.elb-masonry-boss').each(function() {
			var masonryContainer = $(this).find('.elb-masonry-list');
			elb_filter_masonry_options(masonryContainer);
		});

		$('.elb-flt-item:not(.elb-flt-item-tab)').click(function(){
			var filterElement = $(this);
			var filterCat = '.'+filterElement.attr('data-elem');
			var filterCtn = filterElement.parents('.elb-filter-boss');
			filterCtn.find('.elb-flt-item').attr({'data-situation':'inactive'});
			filterElement.attr({'data-situation':'active'});
    		filterCtn.find('.elb-masonry-list').isotope({filter:filterCat});
		});
	};

	var elb_filter_masonry_options = function(masonryContainer){
		var theGutter = masonryContainer.attr('data-gutter');
		var options = {itemSelector: '.elb-masonry-item',transitionDuration:'0',percentPosition: true,masonry:{percentPosition: true, gutterWidth: parseInt(theGutter),
		itemSelector: '.elb-masonry-item', gutter: parseInt(theGutter)}};                    
		imagesLoaded(masonryContainer,function(){masonryContainer.isotope(options);masonryContainer.css({'opacity':'1'}); elb_filter_masonry_show1();});
	}

	var elb_filter_masonry_show1 = function ($scope, $) {
		jQuery('.elb-masonry-item').on('inview', function(isInView) {
			if (isInView)
				jQuery(this).attr({'data-isotope-situation':'shown'})
			else
				jQuery(this).unbind('inview');                              
		});
	};
	
	
	$(window).on('elementor/frontend/init', function () {
		elementorFrontend.hooks.addAction('frontend/element_ready/elb-woomodernproduct-widget.default', elb_filter_masonry_initialize);
		elementorFrontend.hooks.addAction('frontend/element_ready/elb-woolistproduct-widget.default', elb_filter_masonry_initialize);
		elementorFrontend.hooks.addAction('frontend/element_ready/elb-woocategories-widget.default', elb_filter_masonry_initialize);
	});
	
})(jQuery);	
